sysauto_script.script:
// Get the current datetime and the time 24 hours from now
var rightNow = new GlideDateTime();
var oneDayFromNow = new GlideDateTime();
oneDayFromNow.addDaysLocalTime(1);

// Query the database for the needit task table's records
var needItTasksDueSoon = new GlideRecord('x_58872_needit_needit_task');

// State should not be "Closed complete" and due_date should be between current time and 24 hours from now
needItTasksDueSoon.addQuery("state","!=",3);
needItTasksDueSoon.addQuery("due_date",">=",rightNow);
needItTasksDueSoon.addQuery("due_date","<",oneDayFromNow);

needItTasksDueSoon.query();

// Iterating through the results of the query, write a log message for each item
while(needItTasksDueSoon.next()) {
	gs.info("Task due within the next 24 hours: " + needItTasksDueSoon.number);
	gs.eventQueue("x_58872_needit.dueSoonNeedItTasks",needItTasksDueSoon,needItTasksDueSoon.number,gs.getUserName());
}
